<script>
//这里可以导入其他文件（比如：组件，工具js，第三方插件js，json文件，图片文件等等）
//例如：import 《组件名称》 from '《组件路径》';
import Vue from "vue";
import axios from "axios";
Vue.prototype.axios = axios;
// import storage from "best-storage";
// import {chengkou} from "./chengkou.svg";
export default {
  name: "CqZc",
  //import引入的组件需要注入到对象中才能使用
  components: {},
  props: ["county", "cqzc"], //cqzc主城切换子变量
  data() {
    //这里存放数据
    return {
      indexC: 1,
      isMap: false,
      back: require("@static/image/back.png"),
      style: {},
      dw: [
        { name: "龙驹镇", ps: { left: "54%", top: "43%" } }, //
        { name: "金溪镇", ps: { left: "49%", top: "34%" } }, //
        { name: "大进镇", ps: { left: "53%", top: "11%" } }, //
        { name: "后坪乡", ps: { left: "73%", top: "18%" } }, //
        { name: "沿河乡", ps: { left: "20%", top: "25%" } }, //
        { name: "鸡鸣乡", ps: { left: "23%", top: "37%" } }, //
        { name: "三建乡", ps: { left: "40%", top: "26%" } }, //
        { name: "泥溪镇", ps: { left: "52%", top: "51%" } }, //
        { name: "平安乡", ps: { left: "37%", top: "13%" } }, //
        { name: "双龙镇", ps: { left: "52%", top: "18%" } }, //
        { name: "天元乡", ps: { left: "33%", top: "31%" } }, //
        { name: "红池坝经开区", ps: { left: "29%", top: "35%" } }, //
        { name: "车田乡", ps: { left: "29%", top: "21%" } }, //
        { name: "浪坪乡", ps: { left: "60%", top: "14%" } }, //
        { name: "三义乡", ps: { left: "60%", top: "7%" } }, //
        { name: "大垭乡", ps: { left: "27%", top: "59%" } } //
      ],
      year: "",
      isBankuai: false, //是否在板块上
      zcqx: ""
    };
  },
  filters: {
    toFixed: function(value) {
      let aa = parseInt(value.toFixed(2));
      return aa;
    },
    tenThousand: function(value) {
      if (value == "0" || value == 0) {
        return 0;
      }
      if (!value) return "";
      let vv = parseInt(value);
      let cc = (vv / 10000).toFixed(2);
      return cc;
    }
  },
  //监听属性 类似于data概念
  computed: {
    tkd() {
      let a = this.shiGeoCoordMap;
      a = a.map(item => ({
        dw: { left: "20vw", top: "10vh" },
        ...item
      }));
      // console.log(a);
      return a;
    }
  },
  watch: {
    shiGeoCoordMap() {
      let _this = this;
      this.$nextTick(() => {
        if (!this.imgs) {
          this.imgs = Array.from(document.querySelectorAll("image"));
          this.imgs.map(d => {
            d.addEventListener("mousemove", e => {
              let tkdName = e.currentTarget.getAttribute("itt");
              _this.$emit("ycdt", true);
              _this.$emit("codeNmae", tkdName, 1);
            });
            d.addEventListener("mouseout", () => {
              _this.$emit("ycdt", false);
            });
          });
        }
      });
    }
  },
  //方法集合
  methods: {
    close() {
      console.log("1");
      this.$emit("update:cqzc", ++this.indexC);
    },
    isGray(code) {
      let re = true;
      this.county.forEach(item => {
        if (item.CODE == code) {
          re = false;
        }
      });
      return !re;
    },
    tsff(tname) {
      //调试坐标方法
      let aa;
      this.dw.map(item => {
        if (tname == item.name) {
          // aa= JSON.parse(JSON.stringify(item.ps))
          aa = item.ps;
        }
      });
      return aa;
    },
    dwlis(tname) {
      // console.log(tname)
      this.dw.map(item => {
        if (tname == item.name) {
          return item.name;
        }
      });
    },
    sbydjr(item) {
      this.$emit("ycdt", true);
      this.$emit("codeNmae", item.SHORTNAME, 1);
    },
    lkyd() {
      this.$emit("ycdt", false);
    }
  },
  //生命周期 - 创建完成（可以访问当前this实例）
  created() {
    // let yearT =
    //     (storage.session.get("PARAM") &&
    //         storage.session.get("PARAM")["PARAM_YEAR"]) ||
    //     new Date().getFullYear().toString();
    // if (yearT == "all") {
    //     this.year = "所有年度";
    // } else {
    //     this.year = yearT + "年";
    // }
  },
  //生命周期 - 挂载完成（可以访问DOM元素）
  mounted() {
    const _this = this;
    const g = Array.from(document.querySelectorAll("#zhucheng g.zcqClass"));
    g.map(d => {
      d.addEventListener("mouseenter", e => {
        this.isMap = true;
        let codeNmae = Array.from(
          e.currentTarget.querySelectorAll("text tspan")
        );
        let str = codeNmae.map(t => t.innerHTML);
        let a = str.join("");
        _this.$emit("codeNmae", a, 0, this.isMap);
      });
      d.addEventListener("mouseleave", () => {
        console.log('21212');
        // this.isMap = false;
        _this.$emit("ycdt", false);
      });
      d.addEventListener("click", e => {
        let codeNmae = Array.from(
          e.currentTarget.querySelectorAll("text tspan")
        );
        let str = codeNmae.map(t => t.innerHTML);
        let a = str.join("");

        this.county.map(item => {
          if (item.title === a) {
            this.$store.commit("SET_Compartment", {
              code: item.CODE,
              name: item.title
            });
            this.$router.push({
              name: "pageDetail",
              query: {
                id: this.$store.state.previewList.map,
                param: JSON.stringify({ districtid: item.CODE })
              }
            });
          }
        });
      });
    });
  }
};
